function generateCustomizationOptions() {
  const options = {
    theme: getTheme(),
    layout: getLayout('grid'),
    animations: enableAnimations(),
    widgets: ['weather', 'calendar', 'stocks'],
    notifications: activateNotifications('on'),
  };
  applyOptions(options);
}

function getTheme() {
  return 'futuristic';
}

function getLayout(defaultLayout) {
  return defaultLayout || 'list';
}

function enableAnimations() {
  return true;
}

function activateNotifications(status) {
  return status === 'on';
}
